import "./login.css";

import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";

import axios from "axios";

function Login() {
  const navigate = useNavigate();
  const [writtenEmail, setWrittenEmail] = useState("");
  const [writtenPw, setWrittenPw] = useState("");

  async function loginSubmit() {
    await axios
      .post(
        "http://localhost:8000/auth/cookieLogin",
        { email: writtenEmail, pw: writtenPw },
        {
          withCredentials: true,
        }
      ) //
      .then((data) => {
        if (data.data === "success") navigate("/");
        if (data.data === "fail") alert("wrong login information");

        // navigate("/");
      }) //
      .catch((err) => console.log(err));
  }
  return (
    <>
      <h1 className="title">Log-In</h1>

      <div className="container2">
        <Form noValidate>
          <div className="uppersideBtns">
            <Link to="/">
              <Button type="submit" variant="outline-success" className="btn1">
                Home
              </Button>
            </Link>
            <Link to="/signup">
              <Button type="submit" variant="outline-success" className="btn2">
                Sign-up
              </Button>
            </Link>
          </div>
          <Row className="mb-3 ">
            <Form.Group as={Col} md="14">
              <Form.Label>E-mail</Form.Label>
              <Form.Control
                required
                type="text"
                onChange={function (e) {
                  setWrittenEmail(e.target.value);
                }}
              />
            </Form.Group>
          </Row>
          <Row className="mb-3 ">
            <Form.Group as={Col} md="14">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                required
                onChange={function (e) {
                  setWrittenPw(e.target.value);
                }}
              />
            </Form.Group>
          </Row>
          <div className="lowersideBtn">
            <Button
              type="submit"
              onClick={function (e) {
                e.preventDefault();
                loginSubmit();
              }}
              variant="warning"
              className="btn3"
              md="14"
            >
              Submit form(cookie version)
            </Button>
          </div>
        </Form>
      </div>
    </>
  );
}

export default Login;
