import "./readTxt.css";

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.css";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
function ReadTxt() {
  const { id } = useParams();
  const [chosenData, setChosenData] = useState("");
  const [isEditing, setIsEditing] = useState(true);
  const [titleEditing, setTitleEditing] = useState("");
  const [contentEditing, setContentEditing] = useState("");
  useEffect(() => {
    axios
      .get(`http://localhost:8000/readTxt/${id}`) //
      .then((data) => {
        console.log(data.data);
        setChosenData(data.data);
      }) //
      .catch((err) => console.log(err));
  }, []);
  return (
    <>
      <h1 className="title">Text</h1>
      <div className="container">
        <Form>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>제목</Form.Label>
            <Form.Control
              type="title"
              placeholder=""
              disabled={isEditing}
              value={!titleEditing ? chosenData.title : titleEditing}
              onChange={function (e) {
                setTitleEditing(e.target.value);
              }}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>내용</Form.Label>
            <Form.Control
              disabled={isEditing}
              as="textarea"
              rows={10}
              value={!contentEditing ? chosenData.content : contentEditing}
              onChange={function (e) {
                setContentEditing(e.target.value);
              }}
            />
          </Form.Group>
        </Form>
        {isEditing ? (
          <Button
            variant="outline-dark"
            onClick={function () {
              setIsEditing(false);
            }}
            className="btn1"
          >
            Edit
          </Button>
        ) : (
          <Button
            variant="outline-dark"
            onClick={function () {
              console.log(titleEditing, contentEditing);
              axios.post("http://localhost:8000/edit");
            }}
            className="btn1"
          >
            save
          </Button>
        )}

        <Link to="/">
          <Button variant="outline-dark" className="btn2">
            Back
          </Button>
        </Link>
      </div>
    </>
  );
}

export default ReadTxt;
